eMutability":"view","type":"function"},{"inputs":[],"name":"getInitializedVersion","outputs":[{"internalType":"uint8","name":"","type":"uint8"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"operator","type":"address"}],"name":"getRole","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"required","type":"uint256"}],"name":"isFeatureEnabled","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"operator","type":"address"},{"internalType":"uint256","name":"required","type":"uint256"}],"name":"isOperatorInRole","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"required","type":"uint256"}],"name":"isSenderInRole","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"_nftContract","type":"address"},{"internalType":"address","name":"_tierDB","type":"address"}],"name":"postConstruct","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"proxiableUUID","outputs":[{"internalType":"bytes32","name":"","type":"bytes32"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"_mask","type":"uint256"}],"name":"updateFeatures","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"operator","type":"address"},{"internalType":"uint256","name":"role","type":"uint256"}],"name":"updateRole","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"newImplementation","type":"address"}],"name":"upgradeTo","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"newImplementation","type":"address"},{"internalType":"bytes","name":"data","type":"bytes"}],"name":"upgradeToAndCall","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[{"internalType":"uint8","name":"tier","type":"uint8"}],"name":"votingPowerFunction","outputs":[{"internalType":"uint16","name":"","type":"uint16"}],"stateMutability":"pure","type":"function"},{"inputs":[{"internalType":"address","name":"owner","type":"address"}],"name":"votingPowerOf","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"}],"devdoc":{"author":"Basil Gorin","details":"Version 1","kind":"dev","methods":{"evaluateBy(address,uint256,uint256)":{"details":"Calculated based on:      1) operator's own permission set read from userRoles[operator]      2) target permission set - what is already set on the target      3) desired permission set - what do we want set target toCorner cases:      1) Operator is super admin and its permission set is `FULL_PRIVILEGES_MASK`:        `desired` bitset is returned regardless of the `target` permission set value        (what operator sets is what they get)      2) Operator with no permissions (zero bitset):        `target` bitset is returned regardless of the `desired` value        (operator has no authority and cannot modify anything)Example:      Consider an operator with the permissions bitmask     00001111      is about to modify the target permission set          01010101      Operator wants to set that permission set to          00110011      Based on their role, an operator has the permissions      to update only lowest 4 bits on the target, meaning that      high 4 bits of the target set in this example is left      unchanged and low 4 bits get changed as desired:      01010011","params":{"desired":"desired set of permissions operator would like to set","operator":"address of the contract operator which is about to set the permissions","target":"input set of permissions to operator is going to modify"},"returns":{"_0":"resulting set of permissions given operator will set"}},"features()":{"details":"Effectively reads userRoles role for the contract itself","returns":{"_0":"256-bit bitmask of the features enabled"}},"getImplementation()":{"returns":{"_0":"the current implementation address"}},"getInitializedVersion()":{"details":"Highest version that has been initialized.      Non-zero value means contract was already initialized.see {Initializable}, {reinitializer}.","returns":{"_0":"highest version that has been initialized"}},"getRole(address)":{"details":"Having a simple getter instead of making the mapping public      allows enforcing the encapsulation of the mapping and protects from      writing to it directly in the inheriting smart contracts","params":{"operator":"address of a user to read permissions for,      or self address to read global features of the smart contract"}},"isFeatureEnabled(uint256)":{"params":{"required":"set of features to check against"},"returns":{"_0":"true if all the features requested are enabled, false otherwise"}},"isOperatorInRole(address,uint256)":{"params":{"operator":"address of the user to check role for","required":"set of permissions (role) to check"},"returns":{"_0":"true if all the permissions requested are enabled, false otherwise"}},"isSenderInRole(uint256)":{"params":{"required":"set of permissions (role) to check against"},"returns":{"_0":"true if all the permissions requested are enabled, false otherwise"}},"postConstruct(address,address)":{"details":""Constructor replacement" for upgradeable, must be executed immediately after deployment      see https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable#initializers","params":{"_nftContract":"ERC721 contract Voting Logic is bound to","_tierDB":"TierDB contract Voting Logic is bound to"}},"proxiableUUID()":{"details":"Implementation of the ERC1822 {proxiableUUID} function. This returns the storage slot used by the implementation. It is used to validate the implementation's compatibility when performing an upgrade. IMPORTANT: A proxy pointing at a proxiable contract should not be considered proxiable itself, because this risks bricking a proxy that upgrades to it, by delegating to itself until out of gas. Thus it is critical that this function revert if invoked through a proxy. This is guaranteed by the `notDelegated` modifier."},"updateFeatures(uint256)":{"details":"Requires transaction sender to have `ROLE_ACCESS_MANAGER` permissionFunction is left for backward compatibility with older versions","params":{"_mask":"bitmask representing a set of features to enable/disable"}},"updateRole(address,uint256)":{"details":"Setting role to zero is equivalent to removing an all permissionsSetting role to `FULL_PRIVILEGES_MASK` is equivalent to      copying senders' permissions (role) to the userRequires transaction sender to have `ROLE_ACCESS_MANAGER` permission","params":{"operator":"address of a user to alter permissions for,       or self address to alter global features of the smart contract","role":"bitmask representing a set of permissions to      enable/disable for a user specified"}},"upgradeTo(address)":{"custom:oz-upgrades-unsafe-allow-reachable":"delegatecall","details":"Upgrade the implementation of the proxy to `newImplementation`. Calls {_authorizeUpgrade}. Emits an {Upgraded} event."},"upgradeToAndCall(address,bytes)":{"custom:oz-upgrades-unsafe-allow-reachable":"delegatecall","details":"Upgrade the implementation of the proxy to `newImplementation`, and subsequently execute the function call encoded in `data`. Calls {_authorizeUpgrade}. Emits an {Upgraded} event."},"votingPowerFunction(uint8)":{"params":{"tier":"tier of the NFT"},"returns":{"_0":"zero if NFT doesn't exist, its voting power otherwise"}},"votingPowerOf(address)":{"params":{"owner":"address to calculate voting power for"},"returns":{"_0":"cumulative voting power of the address; zero if address doesn't own any NFTs"}}},"stateVariables":{"nftContract":{"details":"ERC721 contract Voting Logic is bound to"},"tierDB":{"details":"Read-only link to the TierDB contract, used to fetch      tier level for the NFTs when calculating voting power"}},"title":"Default Voting Power Logic","version":1},"userdoc":{"kind":"user","methods":{"ROLE_ACCESS_MANAGER()":{"notice":"Access manager is responsible for assigning the roles to users,      enabling/disabling global features of the smart contractAccess manager can add, remove and update user roles,      remove and update global features"},"ROLE_UPGRADE_MANAGER()":{"notice":"Upgrade manager is responsible for smart contract upgrades,      see https://docs.openzeppelin.com/contracts/4.x/api/proxy#UUPSUpgradeable      see https://docs.openzeppelin.com/contracts/4.x/upgradeable"},"evaluateBy(address,uint256,uint256)":{"notice":"Determines the permission bitmask an operator can set on the      target permission setUsed to calculate the permission bitmask to be set when requested     in `updateRole` and `updateFeatures` functions"},"features()":{"notice":"Retrieves globally set of features enabled"},"getImplementation()":{"notice":"Returns an address of the implementation smart contract,      see ERC1967Upgrade._getImplementation()"},"getRole(address)":{"notice":"Reads the permissions (role) for a given user from the `userRoles` mapping"},"isFeatureEnabled(uint256)":{"notice":"Checks if requested set of features is enabled globally on the contract"},"isOperatorInRole(address,uint256)":{"notice":"Checks if operator has all the permissions (role) required"},"isSenderInRole(uint256)":{"notice":"Checks if transaction sender `msg.sender` has all the permissions required"},"updateFeatures(uint256)":{"notice":"Updates set of the globally enabled features (`features`),      taking into account sender's permissions"},"updateRole(address,uint256)":{"notice":"Updates set of permissions (role) for a given user,      taking into account sender's permissions."},"votingPowerFunction(uint8)":{"notice":"Translates the tier to the voting power according to the formula      voting_power(tier) = 2 ^ (tier - 1)      voting power is zero for tier 0 tokens (non-existent tokens)"},"votingPowerOf(address)":{"notice":"Sums up the default voting power of the NFT owner according to the default formula      voting_power(tier) = 2 ^ (tier - 1)Client applications are free to override this and use any formula"}},"notice":"Default voting logic applies to the the owners of NFTs having a tierThe NFT with the tier level `tier` has the voting power      voting_power(tier) = 2 ^ (tier - 1)Voting power of the NFT owner is a sum of voting powers of all the      NFTs owned by the address","version":1}},"settings":{"compilationTarget":{"contracts/protocol/VotingPowerV1.sol":"VotingPowerV1"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"@openzeppelin/contracts-upgradeable/interfaces/IERC1967Upgradeable.sol":{"content":"// SPDX-License-Identifier: MIT
